mapper.select:
select 
count(*)
from tasks
where 1=1
  
    and publisher = #{params.publisher}
  
  
    and surveyor = #{params.surveyor}
  
  
    and executor = #{params.executor}
  
  
    and task_status = #{params.taskStatus}
  
  
    and related_meetings = #{params.relatedMeetings}
  
  
    and acceptance_results = #{params.acceptanceResults}
  
  
    and task_name = #{params.taskName}
  
  
    and publish_time >= #{params.publishTime1}
  
  
    and publish_time <= #{params.publishTime2}
  
  
    and end_time >= #{params.endTime1}
  
  
    and end_time <= #{params.endTime2}
  
  
    order by tasks.start_time asc
  
  
    order by tasks.start_time desc
  
  
    order by tasks.end_time asc
  
  
    order by tasks.end_time desc
  
  
    order by tasks.publish_time asc
  
  
    order by tasks.start_time desc
  
  limit #{params.start},#{params.pageSize}

